import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');

  useEffect(() => {
    axios.get('http://localhost:3000/tasks').then(res => setTasks(res.data));
  }, []);

  const addTask = async () => {
    const res = await axios.post('http://localhost:3000/tasks', { text: newTask, completed: false });
    setTasks([...tasks, res.data]);
    setNewTask('');
  };

  const updateTask = async (task) => {
    const res = await axios.put(`http://localhost:3000/tasks/${task._id}`, {
      ...task,
      completed: !task.completed,
    });
    setTasks(tasks.map(t => (t._id === task._id ? res.data : t)));
  };

  const deleteTask = async (id) => {
    await axios.delete(`http://localhost:3000/tasks/${id}`);
    setTasks(tasks.filter(t => t._id !== id));
  };

  return (
    <div className="App">
      <h1>To-Do App</h1>
      <input
        value={newTask}
        onChange={(e) => setNewTask(e.target.value)}
        placeholder="Add a task"
      />
      <button onClick={addTask}>Add</button>
      <ul>
        {tasks.map(task => (
          <li key={task._id}>
            <input
              type="checkbox"
              checked={task.completed}
              onChange={() => updateTask(task)}
            />
            {task.text}
            <button onClick={() => deleteTask(task._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
